public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3;
		int b=2;
		
		if(a>b) 
		{
			System.out.println("A is Greater than B");
		}
		else 
		{
			System.out.println("B is Greater than A");
		}
	
		
	}

}
